        public static void CalculateDay2()
        {
            Data = Data.Trim().Replace("\r\n", "").Replace("   ", "  ");
            List<int> sides =
                Data.Split(new string[] {"  ",}, StringSplitOptions.None).Select(x => int.Parse(x)).ToList();
            List<TriangleCluster> triangleClusters = new List<TriangleCluster>();
            while (sides.Count >= 9)
            {
                int[] currentNineSides = sides.Take(9).ToArray();
                triangleClusters.Add(new TriangleCluster(currentNineSides));
                sides.RemoveRange(0, 9);
            }

            int totalValidTrianglesCount = 0;

            foreach (TriangleCluster cluster in triangleClusters)
            {
                if (cluster.Triangle1.IsValidTriangle())
                {
                    totalValidTrianglesCount++;
                }
                if (cluster.Triangle2.IsValidTriangle())
                {
                    totalValidTrianglesCount++;
                }
                if (cluster.Triangle3.IsValidTriangle())
                {
                    totalValidTrianglesCount++;
                }
            }
            Console.WriteLine("Total valid triangles in day 2: " + totalValidTrianglesCount);

        }

        public static void CalculateDay1()
        {
            List<Triangle> PossibleTriangles = new List<Triangle>();
            foreach (Triangle triangle in
                Data.Split(new string[] {"\r\n"}, StringSplitOptions.None).Select(x => new Triangle(x)))
            {
                if (triangle.IsValidTriangle())
                {
                    PossibleTriangles.Add(triangle);
                }
            }
            Console.WriteLine("Possible triangles: " + PossibleTriangles.Count);
        }

        public class TriangleCluster
        {
            public Triangle Triangle1;
            public Triangle Triangle2;
            public Triangle Triangle3;

            public TriangleCluster(int[] sides)
            {
                Triangle1 = new Triangle(sides[0], sides[3], sides[6]);
                Triangle2 = new Triangle(sides[1], sides[4], sides[7]);
                Triangle3 = new Triangle(sides[2], sides[5], sides[8]);
            }
        }

        public class Triangle
        {
            public int Side1;
            public int Side2;
            public int Side3;

            public Triangle(int side1, int side2, int side3)
            {
                Side1 = side1;
                Side2 = side2;
                Side3 = side3;
            }

            public Triangle(string sides)
            {
                sides = sides.Replace("   ", "  ");
                string[] split = sides.Split(new string[] {"  ",}, StringSplitOptions.None);
                Side1 = int.Parse(split[1]);
                Side2 = int.Parse(split[2]);
                Side3 = int.Parse(split[3]);
            }

            public bool IsValidTriangle()
            {
                bool s3 = Side1 + Side2 > Side3;
                bool s2 = Side1 + Side3 > Side2;
                bool s1 = Side2 + Side3 > Side1;
                return s3 && s2 && s1;
            }
        }
    }
